current.global_css:
body {
  --input-font-size: calc(var(--base-font-size) - 1px);
  --btn-padding: 0.8rem 3rem;
}
@media (min-width: 992px) {
  body {
    --btn-padding: 1.2rem 4.2rem;
  }
}
.bls__product-item .bls__product-type {
  color: #999999;
}

.bls__product-name a:hover {
  color: var(--primary-color);
}
.bls__product-label>div.bls__pre-order-label {
  background-color: #F8BA26;
  color: var(--heading-color);
}

.bls__product-item .bls__product-type {
  text-transform: uppercase;
}

.bls__product-item .bls__product-price {
  margin-top: 8px;
}

@media (min-width: 768px) {
  .bls__product-item .bls__product-price .price {
    font-size: 18px;
  }

  .bls__product-price .compare-price {
    vertical-align: -3px;
  }

  .template-index .bls__grid .bls__product-item .bls__product-details {
    padding: 15px 20px 15px 20px;
  }

  .bls__footer_block-title.py-10 {
    padding-top: 10px;
  }
}


.quick-search-list li:not(:last-child) a:after {
  transform: translate(-1px);
  -webkit-transform: translateX(-1px);
}
.template-index .bls__grid .bls__product-item .bls__product-details {
    padding: 15px;
	border-top: 1px solid #E5E8EC;
}
.template-index .bls__grid .bls__product-item {
  will-change: transform;
  background-color: var(--color-white);
  border-radius: var(--product-images-style-radius) !important;
  overflow: hidden;
}

.template-index .bls__grid .bls__product-item .bls__product-img,
.template-index .bls__grid .bls__product-item .bls__product-img>a {
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
  border-bottom-left-radius: 0;
  border-bottom-right-radius: 0;
  overflow: hidden;
  position: relative;
  display: block;

}
.bls__before-heading,.bls__before-desc , .bls__before-desc strong{ 
color: #ffffff; }
