{% style %}

  .cm-grid-wrapper {
    width: 100vw;
    margin-left: calc(-50vw + 50%);
    background: linear-gradient(135deg, #00C2FF 0%, #7A66FF 100%);
    padding: 25px 0;
    border-top: 2px solid rgba(255,255,255,0.25);
    border-bottom: 2px solid rgba(255,255,255,0.25);
    box-shadow: 0 0 45px rgba(0, 194, 255, 0.25),
                inset 0 0 45px rgba(122,102,255,0.18);
    isolation: isolate;
  }

  .cyber-grid {
    max-width: 1300px;
    margin: 0 auto;
    padding: 0 22px;
    display: grid;
    grid-template-columns: repeat(6, 1fr);
    gap: 22px;
    font-family: 'Space Grotesk', sans-serif;
  }

  .cyber-square {
    background: rgba(255,255,255,0.10);
    border: 2px solid rgba(255,255,255,0.32);
    border-radius: 16px;
    height: 180px;
    text-decoration: none;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    transform: translateZ(0);
    will-change: transform, box-shadow;
    transition: transform .28s cubic-bezier(.22,.61,.36,1),
                box-shadow .28s ease,
                border-color .28s ease;
    box-shadow: 0 0 28px rgba(0,194,255,0.18),
                inset 0 0 22px rgba(122,102,255,0.18);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
  }

  .cyber-square:hover {
    transform: scale(1.03);
    border-color: #ffffff;
    box-shadow: 0 0 45px rgba(255,255,255,0.32),
                0 0 65px rgba(0,194,255,0.52);
  }

  .cyber-square span.title {
    font-size: 18px;
    font-weight: 800;
    color: #ffffff;
    text-transform: uppercase;
    margin-bottom: 4px;
    text-shadow: 0 0 14px rgba(255,255,255,0.55),
                 0 0 22px rgba(0,194,255,0.35);
    z-index: 2;
  }

  .cyber-square span.subtext {
    font-size: 13px;
    font-weight: 700;
    color: #ffffff;
    opacity: .9;
    text-transform: uppercase;
    z-index: 2;
  }

  .cyber-square::before {
    content: '';
    position: absolute;
    inset: 0;
    background: linear-gradient(135deg, #00C2FF 0%, #7A66FF 100%);
    opacity: 0.10;
    transition: opacity .30s ease;
    z-index: 1;
  }

  .cyber-square:hover::before {
    opacity: 0.35;
  }

  @media(max-width: 992px) {
    .cyber-grid {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media(max-width: 576px) {
    .cyber-square {
      height: 140px;
    }
    .cyber-square span.title {
      font-size: 15px;
    }
    .cyber-square span.subtext {
      font-size: 11px;
    }
  }

{% endstyle %}

