settings.content:
{%- comment -%}
  Banner "Hoy envío gratis" minimalista
  - Umbral: $50.000 (en centavos = 5,000,000)
  - Se evalúa contra el precio con 20% OFF (transferencia): price * 0.8
{%- endcomment -%}

{%- assign threshold_cents = 18000000 -%}
{%- assign context = '' -%}
{%- assign price_cents = 0 -%}

{%- if template.name == 'product' and product -%}
  {%- assign context = 'product' -%}
  {%- assign price_cents = product.selected_or_first_available_variant.price -%}
{%- elsif cart and cart.item_count > 0 -%}
  {%- assign context = 'cart' -%}
  {%- assign price_cents = cart.total_price -%}
{%- endif -%}

{%- comment -%}
  Precio para evaluar el umbral (con 20% OFF):
  - PDP: variant.price * 0.8
  - Cart: subtotal * 0.8  (si NO querés descuento en cart, reemplazá por: assign price_for_threshold_cents = price_cents )
{%- endcomment -%}
{%- assign price_for_threshold_cents = price_cents | times: 80 | divided_by: 100 -%}

{%- assign remaining_cents = threshold_cents | minus: price_for_threshold_cents -%}
{%- if remaining_cents < 0 -%}{%- assign remaining_cents = 0 -%}{%- endif -%}

<style>
  .free-ship-minimal{
    padding:1rem;border:1px solid #e5e5e5;border-radius:12px;
    font-size:1.2rem;line-height:1.4;text-align:center;
    background:#fafafa;color:#111;margin:.5rem 0;
  }
  .free-ship-strong{font-weight:600;}
</style>

<div class="free-ship-minimal">
  {%- if context != '' -%}
    {%- if price_for_threshold_cents >= threshold_cents -%}
      <span class="free-ship-strong">Hoy envío gratis</span> en este producto.
    {%- else -%}
      {%- assign remaining_money = remaining_cents | money_without_trailing_zeros -%}
      <span>Envío gratis desde ${{ threshold_cents | money_without_trailing_zeros }}.</span><br>
      Te faltan <span class="free-ship-strong">{{ remaining_money }}</span> para alcanzarlo.
    {%- endif -%}
  {%- else -%}
    <span>Hoy envío gratis en pedidos desde <span class="free-ship-strong">${{ threshold_cents | money_without_trailing_zeros }}</span>.</span>
  {%- endif -%}
</div>

{% assign v = product.selected_or_first_available_variant %}

{% if v.compare_at_price > v.price %}
  <div class="precio-anterior">
    Precio anterior: <s>{{ v.compare_at_price | money }}</s>
  </div>
{% endif %}

<style>
  .precio-anterior {
    font-size: 1.75rem; /* grande */

    color: #555555; /* gris */
  }
  .precio-anterior s {
    text-decoration: line-through;
  }
</style>

{% if product and product.selected_or_first_available_variant %}

{%- comment -%} === VARIABLES DE PRECIO === {%- endcomment -%}
{%- assign base_price   = product.selected_or_first_available_variant.price -%}
{%- assign price_off_20 = base_price | times: 80 | divided_by: 100 -%}
{%- assign price_off_10 = base_price | times: 90 | divided_by: 100 -%}
{%- assign cuota_6     = base_price | divided_by: 6 -%}
{%- assign cuota_3      = price_off_10 | divided_by: 3 -%}



{%- comment -%} === LÓGICA DE ENTREGA NACIONAL === {%- endcomment -%}
{%- assign current_date = "now" | date: "%s" -%}
{%- assign days_added = 0 -%}
{%- assign min_days = 3 -%}
{%- assign max_days = 7 -%}
{%- assign min_delivery = current_date -%}
{%- assign max_delivery = current_date -%}

{%- comment -%} Calcular día mínimo de entrega {%- endcomment -%}
{%- for i in (1..10) -%}
    {%- assign min_delivery = min_delivery | plus: 86400 -%}
    {%- assign min_day = min_delivery | date: "%A" -%}
    {%- unless min_day == "Saturday" or min_day == "Sunday" -%}
        {%- assign days_added = days_added | plus: 1 -%}
    {%- endunless -%}
    {%- if days_added == min_days -%}
        {%- break -%}
    {%- endif -%}
{%- endfor -%}

{%- comment -%} Calcular día máximo de entrega {%- endcomment -%}
{%- assign days_added = 0 -%}
{%- for i in (1..15) -%}
    {%- assign max_delivery = max_delivery | plus: 86400 -%}
    {%- assign max_day = max_delivery | date: "%A" -%}
    {%- unless max_day == "Saturday" or max_day == "Sunday" -%}
        {%- assign days_added = days_added | plus: 1 -%}
    {%- endunless -%}
    {%- if days_added == max_days -%}
        {%- break -%}
    {%- endif -%}
{%- endfor -%}

{%- comment -%} Traducir días al español {%- endcomment -%}
{%- assign min_day_number = min_delivery | date: "%d" -%}
{%- assign max_day_number = max_delivery | date: "%d" -%}
{%- assign min_day = min_delivery | date: "%A" -%}
{%- assign max_day = max_delivery | date: "%A" -%}

{%- assign days_dict = "Sunday:Domingo,Monday:Lunes,Tuesday:Martes,Wednesday:Miércoles,Thursday:Jueves,Friday:Viernes,Saturday:Sábado" | split: "," -%}

{%- for day in days_dict -%}
    {%- assign eng = day | split: ":" | first -%}
    {%- assign esp = day | split: ":" | last -%}
    {%- if min_day == eng -%}
        {%- assign min_day = esp -%}
    {%- endif -%}
    {%- if max_day == eng -%}
        {%- assign max_day = esp -%}
    {%- endif -%}
{%- endfor -%}

{%- assign mensaje_entrega_nacional = "Te llega entre el <strong style='color: green;'>" | append: min_day | append: " " | append: min_day_number | append: "</strong> y el <strong style='color: green;'>" | append: max_day | append: " " | append: max_day_number | append: "</strong>" -%}

